require 'spec_helper'describe 'role_ggonda_cassandra' do  context 'on a supported OS' do    let(:facts) do      {        :osfamily        => 'RedHat',        :operatingsystem => 'RedHat',        :architecture    => 'x86_64',        :ipaddress       => '192.168.1.100'      }    end    it { is_expected.to compile.with_all_deps }    it { is_expected.to contain_class('role_ggonda_cassandra') }    it { is_expected.to contain_class('profile_ggonda_cassandr') }  end  context 'on an unsupported OS' do    let(:facts) do      {        :osfamily        => 'Unsupported',        :operatingsystem => 'Unsupported',        :architecture    => 'x86_64',        :ipaddress       => '192.168.1.100'      }    end    it { is_expected.to compile.with_all_deps }  endend